import React, { useState } from "react";
import { AnimatePresence, motion } from "framer-motion";
import {
  FiAlertCircle,
  FiArchive,
  FiGithub,
  FiAtSign,
  FiLinkedin,
} from "react-icons/fi";

const Info = () => {
  // const [showEmail, setShowEmail] = useState(false);
  // const [showGithub, setShowGithub] = useState(false);
  // const [showLinkedin, setShowLinkedin] = useState(false);

  const [heading, setHeading] = useState(0)
  const [section, setSection] = useState({})

  const titles = [
    {id: 1, name: 'about', title: 'about me.', link: '', icon: () => {return <FiAlertCircle/>}},
    {id: 2, name: 'projects', title: 'about my work.', link: '', icon: () => {return <FiArchive/>}},
    {id: 3, name: 'github', title: 'browse my github.', link: 'http://www.github.com/skhemnan', icon: () => {return <FiGithub/>}},
    {id: 4, name: 'linkedin', title: 'visit my linkedin.', link: 'http://www.github.com/in/hellosaahil', icon: () => {return <FiLinkedin/>}},
    {id: 5, name: 'email', title: 'send me an email.', link: 'mailto:saahil.khemlani@gmail.com', icon: () => {return <FiAtSign/>}}
  ]

  return (
    <>
        <div>
          <div class="info">
            {(heading === 0 && Object.keys(section).length === 0) ? 
              <>
                <h1 class="main-title">Hello. I'm Saahil.</h1>
                <h2 className="subtitle">
                  App Developer. Product Analyst. Tech enthusiast.
                </h2>
              </>
              :
              <>
                {titles.map(x => {
                  if(x.id === heading){
                    return <h1 class="title">{x.title}</h1>
                  }
                })} 
              </>
            }
            <div className="social-container">
              {titles.map(x => {
                return (
                  <div className="social-links">
                    <a
                      onMouseEnter={() => setHeading(x.id)}
                      onMouseLeave={() => setHeading(0)}
                      className="social-link"
                      href={x.link}
                      onClick={() => {console.log('setting section to: ', x.name)}}
                    >{x.icon}</a> 
                    {/* <a class="social-text" href={x.link}>{x.title}</a> */}
                  </div>
                )
              })}
           </div>
          </div>
        </div>
    </>
  );
};

export default Info;
